dataSet.query:
SELECT "Assets" AS Division, "Current Assets" AS Section, 1 AS SectionOrder INTO BalanceSections
UNION ALL
SELECT "Assets", "Noncurrent Assets", 2
UNION ALL
SELECT "Liabilities and Stockholder Equity", "Current Liabilities", 3
UNION ALL
SELECT "Liabilities and Stockholder Equity", "Long Term Liabilities", 4
UNION ALL
SELECT "Liabilities and Stockholder Equity", "Stockholder Equity", 5 ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED SystemTitle.Value AS OurCompany, BalanceSections.Division, BalanceSections.Section, BalanceSections.SectionOrder, GeneralJournalBalanceAndTurnovers.Account.AccountType AS AccountType, GeneralJournalBalanceAndTurnovers.Account AS Account, GeneralJournalBalanceAndTurnovers.DayPeriod AS DayPeriod, GeneralJournalBalanceAndTurnovers.WeekPeriod AS WeekPeriod, GeneralJournalBalanceAndTurnovers.MonthPeriod AS MonthPeriod, GeneralJournalBalanceAndTurnovers.QuarterPeriod AS QuarterPeriod, GeneralJournalBalanceAndTurnovers.YearPeriod AS YearPeriod, CASE WHEN NOT GeneralJournalBalanceAndTurnovers.Account IS NULL THEN GeneralJournalBalanceAndTurnovers.AmountRCClosingBalance ELSE 0 END AS AmountRCBalance, CASE WHEN NOT GeneralJournalBalanceAndTurnovers.Account IS NULL THEN GeneralJournalBalanceAndTurnovers.AmountRCOpeningBalance ELSE 0 END AS AmountRCOpeningBalance, CASE WHEN NOT GeneralJournalBalanceAndTurnovers.Account IS NULL THEN GeneralJournalBalanceAndTurnovers.AmountRCTurnover ELSE 0 END AS AmountRCTurnover {SELECT AccountType.*, Account.*, YearPeriod, QuarterPeriod, MonthPeriod, WeekPeriod, DayPeriod} FROM BalanceSections AS BalanceSections LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) LEFT JOIN AccountingRegister.GeneralJournal.BalanceAndTurnovers(, , Auto, RegisterRecordsAndPeriodBoundaries, , , ) AS GeneralJournalBalanceAndTurnovers ON (CASE WHEN GeneralJournalBalanceAndTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Bank), VALUE(Enum.AccountTypes.Inventory), VALUE(Enum.AccountTypes.AccountsReceivable), VALUE(Enum.AccountTypes.OtherCurrentAsset)) THEN "Current Assets" WHEN GeneralJournalBalanceAndTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.FixedAsset), VALUE(Enum.AccountTypes.AccumulatedDepreciation), VALUE(Enum.AccountTypes.OtherNoncurrentAsset)) THEN "Noncurrent Assets" WHEN GeneralJournalBalanceAndTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.AccountsPayable), VALUE(Enum.AccountTypes.OtherCurrentLiability)) THEN "Current Liabilities" WHEN GeneralJournalBalanceAndTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.LongTermLiability)) THEN "Long Term Liabilities" WHEN GeneralJournalBalanceAndTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Equity)) THEN "Stockholder Equity" WHEN GeneralJournalBalanceAndTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) AND GeneralJournalBalanceAndTurnovers.Account.OtherComprehensiveIncome = FALSE THEN "Stockholder Equity" ELSE NULL END = BalanceSections.Section) {WHERE GeneralJournalBalanceAndTurnovers.Account.AccountType AS AccountType, GeneralJournalBalanceAndTurnovers.Account AS Account, GeneralJournalBalanceAndTurnovers.YearPeriod AS YearPeriod, GeneralJournalBalanceAndTurnovers.QuarterPeriod AS QuarterPeriod, GeneralJournalBalanceAndTurnovers.MonthPeriod AS MonthPeriod, GeneralJournalBalanceAndTurnovers.WeekPeriod AS WeekPeriod, GeneralJournalBalanceAndTurnovers.DayPeriod AS DayPeriod}
